create database if not exists blog_db;
USE blog_db;

DROP TABLE IF EXISTS posts;
DROP TABLE IF EXISTS users;
SHOW TABLES;

CREATE TABLE users (
                    email text not null,
                    username text not null,
                    password text not null
                    );
CREATE TABLE posts
                (
                 id INT UNSIGNED NOT NULL AUTO_INCREMENT,
                title VARCHAR(240) NOT NULL,
                body  TEXT         NOT NULL,
                PRIMARY KEY (id)
                );
